$(function () {
    function resize() {
        $("#list1").attr("size", Math.max($("option", "#list1").length, 2));
        $("#list2").attr("size", Math.max($("option", "#list2").length, 2));
    }
    $("#list1").dblclick(function () {
        $("option:selected", this).appendTo("#list2");
        resize();
    });
    $("#list2").dblclick(function () { 
        $("option:selected", this).appendTo("#list1");
        resize();
    });
});